import React from 'react';
import style from "./App.module.css"
import TodoList from './TodoList/TodoList';

const App = () => {

  debugger;
  let todos = [
    {id: 1, name : "Make America great again!", completed : false},
    {id: 2, name : "Eat Nuts", completed : false},
    {id: 3, name : "Play Minecraft", completed : false}
  ]

  let [Todos, SetTodos] = React.useState(todos)

  let OnChange = (id) => {
    SetTodos(
      Todos.map((td) => {
        if(td.id === id){
          td.completed = !td.completed
        }
        console.log("New");
        return td;
      })
    );
  }

  return (
    <div className={style.wrapper}>
      <TodoList todo={todos} OnToggle={OnChange}/>
    </div>
  );
}

export default App;
