import React from 'react';
import style from "./App.module.css"
import TodoList from './TodoList/TodoList';

const App = () => {


  let todos = [
    {id: 1, name : "Make America great again!", comleted : false},
    {id: 2, name : "Eat Nuts", comleted : false},
    {id: 3, name : "Play Minecraft", comleted : false}
  ]

  let [Todos, SetTodos] = React.useState(todos)

  let OnChange = (id) => {
    SetTodos(
      Todos.map((td) => {
        if(td.id === id){
          td.comleted = !td.comleted
        }
      })
    );
  }

  return (
    <div className={style.wrapper}>
      <TodoList todo={todos} OnToggle={OnChange}/>
    </div>
  );
}

export default App;
